// src/firebaseConfig.js
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, signInWithCustomToken, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, doc, getDoc, addDoc, setDoc, updateDoc, deleteDoc, onSnapshot, collection, query, where, getDocs } from 'firebase/firestore';

// Your web app's Firebase configuration
// Replace these with your actual Firebase project configuration
// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
  apiKey: "AIzaSyAioe1ey0EG9ZbiZ5ppuZiyYr_DG06g4UQ",
  authDomain: "teacher-toolbox-app.firebaseapp.com",
  projectId: "teacher-toolbox-app",
  storageBucket: "teacher-toolbox-app.firebasestorage.app",
  messagingSenderId: "888087168438",
  appId: "1:888087168438:web:6e5986cdecccc5c743ed17",
  measurementId: "G-QKWBYGD4ZR"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);

// Export Firebase services and functions for use in other components
export {
  app,
  auth,
  db,
  signInAnonymously,
  signInWithCustomToken,
  onAuthStateChanged,
  doc,
  getDoc,
  addDoc,
  setDoc,
  updateDoc,
  deleteDoc,
  onSnapshot,
  collection,
  query,
  where,
  getDocs
};

// For local development, you might want to define a fixed app ID
// This is used for Firestore collection paths like `artifacts/${appId}/users/${userId}/...`
// In a real production app, this might come from environment variables or be part of your app's deployment.
export const APP_ID_FOR_FIRESTORE = "teacher-toolbox-app"; // You can change this to any unique string
